 *
 * Can be used inside an OrbScene for composed backgrounds,
 * or standalone for individual orb effects.
 */
export function Orb({
  color,
  position = [0.5, 0.5],
  size = 0.75,
  blur = 40,
  blendMode = 'screen',
  wavy: _wavy,
  drift: _drift,
  renderer: _renderer,
  interactive: _interactive,
