{
  "name": "orbkit",
  "version": "0.0.1",
  "type": "module",
  "description": "Composable, animated orb effects for React",
  "author": "Oryan Moshe",
  "license": "MIT",
  "repository": {
    "type": "git",
    "url": "https://github.com/oryanmoshe/orbkit.git",
    "directory": "packages/core"
  },
  "keywords": ["react", "orb", "gradient", "animation", "webgl", "background"],
  "main": "./dist/cjs/index.cjs",
  "module": "./dist/esm/index.js",
  "types": "./dist/types/index.d.ts",
  "exports": {
    ".": {
      "import": {
        "types": "./dist/types/index.d.ts",
        "default": "./dist/esm/index.js"
      },
      "require": {
        "types": "./dist/types/index.d.cts",
        "default": "./dist/cjs/index.cjs"
      }
    }
  },
  "files": ["dist"],
  "sideEffects": false,
  "scripts": {
    "build": "bun run build:esm && bun run build:cjs && bun run build:types",
    "build:esm": "bun build src/index.ts --outdir dist/esm --format esm --external react --external react-dom",
    "build:cjs": "bun build src/index.ts --outdir dist/cjs --format cjs --external react --external react-dom",
    "build:types": "tsc --emitDeclarationOnly --outDir dist/types",
    "test": "bun test",
    "typecheck": "tsc --noEmit"
  },
  "peerDependencies": {
    "react": "^18.0.0 || ^19.0.0",
    "react-dom": "^18.0.0 || ^19.0.0"
  },
  "devDependencies": {
    "@types/react": "^18.3.0",
    "@types/react-dom": "^18.3.0",
    "react": "^18.3.0",
    "react-dom": "^18.3.0"
  }
}

