 1→import type { HslColor } from '../types';
 2→
 3→/**
 4→ * Convert a hex color string (#RRGGBB) to HSL.
 5→ */
 6→export function hexToHsl(hex: string): HslColor {
 7→  const cleaned = hex.replace('#', '');
 8→  const r = Number.parseInt(cleaned.substring(0, 2), 16) / 255;
 9→  const g = Number.parseInt(cleaned.substring(2, 4), 16) / 255;
10→  const b = Number.parseInt(cleaned.substring(4, 6), 16) / 255;
11→
12→  const max = Math.max(r, g, b);
13→  const min = Math.min(r, g, b);
14→  const l = (max + min) / 2;
15→
16→  if (max === min) {
17→    return { h: 0, s: 0, l: l * 100 };
18→  }
19→
20→  const d = max - min;
21→  const s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
22→
23→  let h: number;
24→  switch (max) {
25→    case r:
26→      h = ((g - b) / d + (g < b ? 6 : 0)) / 6;
27→      break;
28→    case g:
29→      h = ((b - r) / d + 2) / 6;
30→      break;
31→    default:
32→      h = ((r - g) / d + 4) / 6;
33→      break;
34→  }
35→
36→  return { h: h * 360, s: s * 100, l: l * 100 };
37→}
38→
39→function hueToRgb(p: number, q: number, t: number): number {
40→  let adjusted = t;
41→  if (adjusted < 0) adjusted += 1;
42→  if (adjusted > 1) adjusted -= 1;
43→  if (adjusted < 1 / 6) return p + (q - p) * 6 * adjusted;
44→  if (adjusted < 1 / 2) return q;
45→  if (adjusted < 2 / 3) return p + (q - p) * (2 / 3 - adjusted) * 6;
46→  return p;
47→}
48→
49→/**
50→ * Convert HSL values to a hex color string (#RRGGBB).
51→ */
52→export function hslToHex(h: number, s: number, l: number): string {
53→  const sNorm = s / 100;
54→  const lNorm = l / 100;
55→
56→  let r: number;
57→  let g: number;
58→  let b: number;
59→
60→  if (sNorm === 0) {
61→    r = g = b = lNorm;
62→  } else {
63→    const q = lNorm < 0.5 ? lNorm * (1 + sNorm) : lNorm + sNorm - lNorm * sNorm;
64→    const p = 2 * lNorm - q;
65→    r = hueToRgb(p, q, h / 360 + 1 / 3);
66→    g = hueToRgb(p, q, h / 360);
67→    b = hueToRgb(p, q, h / 360 - 1 / 3);
68→  }
69→
70→  const toHex = (c: number) =>
71→    Math.round(c * 255)
72→      .toString(16)
73→      .padStart(2, '0');
74→
75→  return `#${toHex(r)}${toHex(g)}${toHex(b)}`;
76→}
