15→/** Generate the full CSS background string for a theme */
16→export function generateGradientCSS(
17→  points: Array<{ color: string; position: [number, number]; radius: number }>,
18→  saturation: number,
19→): string {
20→  return points.map((point) => generateOrbCSS(point, saturation)).join(', ');
