 1→import { type JSX, useCallback, useEffect, useRef } from 'react';
 2→import type { GrainProps } from '../types';
 3→
 4→/**
 5→ * Grain — A canvas-based noise overlay.
 6→ *
 7→ * Renders random grayscale noise onto a canvas element
 8→ * with mix-blend-mode: overlay for a film grain effect.
 9→ */
10→export function Grain({ intensity = 0.35, className, style }: GrainProps): JSX.Element {
11→  const canvasRef = useRef<HTMLCanvasElement>(null);
12→
13→  const renderNoise = useCallback(() => {
14→    const canvas = canvasRef.current;
15→    if (!canvas) return;
16→
17→    const ctx = canvas.getContext('2d');
18→    if (!ctx) return;
19→
20→    const { width, height } = canvas.getBoundingClientRect();
21→    canvas.width = width;
22→    canvas.height = height;
23→
24→    const imageData = ctx.createImageData(width, height);
25→    const { data } = imageData;
26→
27→    for (let i = 0; i < data.length; i += 4) {
28→      const value = Math.random() * 255;
29→      data[i] = value;
30→      data[i + 1] = value;
31→      data[i + 2] = value;
32→      data[i + 3] = 255;
33→    }
34→
35→    ctx.putImageData(imageData, 0, 0);
36→  }, []);
37→
38→  useEffect(() => {
39→    renderNoise();
40→
41→    const handleResize = () => renderNoise();
42→    window.addEventListener('resize', handleResize);
43→    return () => window.removeEventListener('resize', handleResize);
44→  }, [renderNoise]);
45→
46→  // Map intensity 0-1 to opacity 0-0.5
47→  const opacity = intensity * 0.5;
48→
49→  return (
50→    <canvas
51→      ref={canvasRef}
52→      className={className ? `orbkit-grain ${className}` : 'orbkit-grain'}
53→      style={{
54→        position: 'absolute',
55→        inset: 0,
56→        width: '100%',
57→        height: '100%',
58→        mixBlendMode: 'overlay',
59→        opacity,
60→        pointerEvents: 'none',
61→        ...style,
62→      }}
63→    />
64→  );
65→}
