80→ * @param hex - Hex color string
81→ * @param saturation - Target saturation 0-100
82→ */
83→export function applySaturation(hex: string, saturation: number): string {
84→  const hsl = hexToHsl(hex);
85→  return hslToHex(hsl.h, saturation, hsl.l);
86→}
