 33→ * @param breathing - Global animation intensity 0-100
 34→ */
 35→export function getOrbitParams(
 36→  x: number,
 37→  y: number,
 38→  index: number,
 39→  breathing: number,
 40→): OrbitParams {
 41→  const seed = positionSeed(x, y);
 42→  const rand1 = seededRandom(seed);
 43→  const rand2 = seededRandom(seed + 1);
 44→
 45→  const breathingFactor = breathing / 100;
 46→  const amplitudeRange = MAX_AMPLITUDE - MIN_AMPLITUDE;
 47→
 48→  const amplitudeX = MIN_AMPLITUDE + rand1 * amplitudeRange * breathingFactor;
 49→  const amplitudeY = MIN_AMPLITUDE + rand2 * amplitudeRange * breathingFactor;
 50→
 51→  const durationRange = MAX_DURATION - MIN_DURATION;
 52→  const baseDuration = MIN_DURATION + (1 - breathingFactor) * durationRange;
 53→
 54→  // Each successive orb is 30% slower for visual depth
 55→  const duration = baseDuration * (1 + index * 0.3);
 56→
 57→  // Staggered start for organic feel
 58→  const delay = index * -baseDuration * 0.25;
 59→
 60→  return { amplitudeX, amplitudeY, duration, delay };
 61→}
 62→
 63→/**
 64→ * Generate CSS keyframe values for an elliptical drift orbit.
 65→ *
 66→ * Returns an array of 9 keyframe stops (0% through 100%)
 67→ * using the 8-step elliptical path from the source implementation.
 68→ */
 69→export function generateDriftKeyframes(
 70→  amplitudeX: number,
 71→  amplitudeY: number,
 72→): Array<{ offset: number; transform: string }> {
 73→  const cos45 = Math.SQRT1_2;
 74→
 75→  return [
 76→    { offset: 0, transform: `translate(${amplitudeX}%, 0)` },
 77→    { offset: 0.125, transform: `translate(${amplitudeX * cos45}%, ${amplitudeY * cos45}%)` },
 78→    { offset: 0.25, transform: `translate(0, ${amplitudeY}%)` },
 79→    { offset: 0.375, transform: `translate(${-amplitudeX * cos45}%, ${amplitudeY * cos45}%)` },
 80→    { offset: 0.5, transform: `translate(${-amplitudeX}%, 0)` },
 81→    { offset: 0.625, transform: `translate(${-amplitudeX * cos45}%, ${-amplitudeY * cos45}%)` },
 82→    { offset: 0.75, transform: `translate(0, ${-amplitudeY}%)` },
 83→    { offset: 0.875, transform: `translate(${amplitudeX * cos45}%, ${-amplitudeY * cos45}%)` },
 84→    { offset: 1, transform: `translate(${amplitudeX}%, 0)` },
 85→  ];
 86→}
 87→
 88→/**
 89→ * Generate a CSS @keyframes string for an elliptical drift orbit.
 90→ */
 91→export function generateDriftKeyframeCSS(name: string, ax: number, ay: number): string {
 92→  const cos45 = Math.SQRT1_2;
 93→
 94→  return `@keyframes ${name} {
 95→  0% { transform: translate(${ax}%, 0); }
 96→  12.5% { transform: translate(${ax * cos45}%, ${ay * cos45}%); }
 97→  25% { transform: translate(0, ${ay}%); }
 98→  37.5% { transform: translate(${-ax * cos45}%, ${ay * cos45}%); }
 99→  50% { transform: translate(${-ax}%, 0); }
100→  62.5% { transform: translate(${-ax * cos45}%, ${-ay * cos45}%); }
101→  75% { transform: translate(0, ${-ay}%); }
102→  87.5% { transform: translate(${ax * cos45}%, ${-ay * cos45}%); }
103→  100% { transform: translate(${ax}%, 0); }
104→}`;
105→}
