29→```typescript
30→// utils/keyframe-registry.ts
31→const injectedKeyframes = new Set<string>();
32→
33→export function injectKeyframes(name: string, css: string): void {
34→  if (injectedKeyframes.has(name)) return;
35→  if (typeof document === 'undefined') return; // SSR guard
36→
37→  const style = document.createElement('style');
38→  style.setAttribute('data-orbkit', name);
39→  style.textContent = css;
40→  document.head.appendChild(style);
41→  injectedKeyframes.add(name);
42→}
43→
44→export function removeKeyframes(name: string): void {
45→  const el = document.querySelector(`style[data-orbkit="${name}"]`);
46→  if (el) el.remove();
47→  injectedKeyframes.delete(name);
48→}
49→```
