57→const [animationProps, setAnimationProps] = useState<{
58→  animationName: string;
59→  duration: number;
60→  delay: number;
61→} | null>(null);
62→
63→useEffect(() => {
64→  if (!drift) {
65→    setAnimationProps(null);
66→    return;
67→  }
68→
69→  const { keyframeCSS, animationName, duration, delay } = generateOrbAnimation(
70→    { color, position, size },
71→    orbIndex,    // from scene context
72→    breathing    // from scene context or prop
73→  );
74→
75→  injectKeyframes(animationName, keyframeCSS);
76→  setAnimationProps({ animationName, duration, delay });
77→
78→  return () => removeKeyframes(animationName);
79→}, [drift, position, breathing, orbIndex]);
80→```
