73→// Simplex noise 3D (Ashima Arts / Ian McEwan)
74→// ~40 lines, well-established implementation
75→
76→// Fractal Brownian Motion
77→float fbm(vec3 p) {
78→  float value = 0.0;
79→  float amplitude = 0.5;
80→  for (int i = 0; i < 4; i++) {
81→    value += amplitude * snoise(p);
82→    p *= 2.0;
83→    amplitude *= 0.5;
84→  }
85→  return value;
86→}
